import 'bootstrap/dist/css/bootstrap.css';
import React, {useState, useEffect} from 'react'
import FullCalendar, { formatDate } from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'
import timeGridPlugin from '@fullcalendar/timegrid'
import interactionPlugin from '@fullcalendar/interaction'
import {Button, Modal, ModalHeader, ModalBody, ModalFooter} from "reactstrap";
import {useForm} from "react-hook-form";
import {CalendarService} from "../../Services/CalendarService";

export default function Calendar() {
    //hook para el formulario
    const { register, handleSubmit} = useForm();

    const { selectInfo, setSelectInfo} = useForm();

    //Hook de estado para cargar los datos de las citas
    const [events, setEvents] = useState([]);

    //Hook de estado para abrir o cerrar la ventana modal
    const [state = false, setState] = useState();

    const [daySelected, setDaySelected] = useState();

    //opciones de los datos que muestra el componente full calendar
    const options = {
        plugins: [dayGridPlugin, timeGridPlugin, interactionPlugin],
        displayEventEnd: true,
        defaultView: 'dayGridMonth',
        defaultDate: '2021-01-01',
        header: {
            left: 'prev, next',
            center: 'title',
            right: 'dayGridMonth, t1meGr1dWeek, t1meGr1dDay'
        },
        editable: false,
    };

    //funcion para abrir o cerrar el modal
    const openModal = () => {

        setState(!state);
    }

    //instancia del servicio de peticiones al back
    const calendarService = new CalendarService();

    //peticion get axios para traer los datos del back
    useEffect(() => {
        calendarService.getEvents().then(data => setEvents(data));
    }, []);// eslint-disable-line react-hooks/exhaustive-deps

    //peticion post de axios para guardar una nueva alarma
    const onSubmit = form => {
        calendarService.setEvents(form).then(data => setEvents(data));
        openModal()
    }

    const handleDateSelect = (selectInfo) => {
        daySelected = selectInfo.startStr;
        openModal()
    }

    return(
        <div>
            <div className="class">
                <FullCalendar
                    plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
                    headerToolbar={{
                        left: 'prev,next today',
                        center: 'title',
                        right: 'dayGridMonth,timeGridWeek,timeGridDay'
                    }}
                    initialView='dayGridMonth'
                    editable={true}
                    selectable={true}
                    selectMirror={true}
                    dayMaxEvents={true}
                    events={events}
                    options={options}
                    select={handleDateSelect}
                />
                <Modal isOpen={state}>
                    <ModalHeader>
                        Add Alarm in day {daySelected}
                    </ModalHeader>
                    <ModalBody>
                        <form className="form"  onSubmit={handleSubmit(onSubmit)}>
                            <input name="date" id="date" type="date" value={daySelected} readOnly={true} ref={register()}/>
                            <input name="title" id="title" type="time" placeholder="Add name of alarm" ref={register()}/>
                            <input name="start" id="start" type="time" placeholder="2021-01-01T16:00:00" ref={register()}/>
                            <input name="end" id="end" type="datetime" placeholder="2021-01-01T16:30:00" ref={register()}/>
                        </form>
                    </ModalBody>
                    <ModalFooter>
                        <Button color="primary" onClick={handleSubmit(onSubmit)}>Save</Button>
                        <Button color="secondary" onClick={openModal}>Close</Button>
                    </ModalFooter>
                </Modal>
            </div>

        </div>
    )
}
