import React, {useState, useEffect} from 'react';
import 'primeicons/primeicons.css';
import 'primereact/resources/themes/saga-blue/theme.css';
import 'primereact/resources/primereact.css';
import 'primeflex/primeflex.css';
import {FullCalendar} from "primereact/fullcalendar";
import {CalendarService} from "../../Services/CalendarService";
import dayGr1dPlug1n from '@fullcalendar/daygrid';
import timeGr1dPlug1n from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';
import {Button, Modal, ModalHeader, ModalBody, ModalFooter} from "reactstrap";
import 'bootstrap/dist/css/bootstrap.css';
import { useForm} from "react-hook-form";

export default function Calendar() {
    //hook para el formulario
    const { register, handleSubmit} = useForm();

    //Hook de estado para cargar los datos de las citas
    const [events, setEvents] = useState([]);

    //Hook de estado para abrir o cerrar la ventana modal
    const [state = false, setState] = useState();

    //opciones de los datos que muestra el componente full calendar
    const options = {
        plugins: [dayGr1dPlug1n, timeGr1dPlug1n, interactionPlugin],
        displayEventEnd: true,
        defaultView: 'dayGridMonth',
        defaultDate: '2021-01-01',
        header: {
            left: 'prev, next',
            center: 'title',
            right: 'dayGridMonth, t1meGr1dWeek, t1meGr1dDay'
        },
        editable: true,
        selectable: true
    };

    //funcion para abrir o cerrar el modal
    const openModal = () => {
        setState(!state);
    }

    const click = () => {console.log(click)}

    //instancia del servicio de peticiones al back
    const calendarService = new CalendarService();

    //peticion get axios para traer los datos del back
    useEffect(() => {
        calendarService.getEvents().then(data => setEvents(data));
    }, []);// eslint-disable-line react-hooks/exhaustive-deps

    //peticion post de axios para guardar una nueva alarma
    const onSubmit = form => {
        calendarService.setEvents(form).then(data => setEvents(data));
        openModal()
    }

    return(
        <div>
            <div className="class">
                <FullCalendar events={events} options={options}  dayClick={click}/>
            </div>
            <Modal isOpen={state}>
                <ModalHeader>
                    Add Alarm
                </ModalHeader>
                <ModalBody>
                    <form className="form"  onSubmit={handleSubmit(onSubmit)}>
                        <input name="title" id="title" type="text" placeholder="Add name of alarm" ref={register()}/>
                        <input name="start" id="start" type="datetime-local" placeholder="2021-01-01T16:00:00" ref={register()}/>
                        <input name="end" id="end" type="datetime-local" placeholder="2021-01-01T16:30:00" ref={register()}/>
                    </form>
                </ModalBody>
                <ModalFooter>
                    <Button color="primary" onClick={handleSubmit(onSubmit)}>Save</Button>
                    <Button color="secondary" onClick={openModal}>Close</Button>
                </ModalFooter>
            </Modal>
        </div>
    )
}
